import React, { Component } from 'react'

export default class AuthComponent extends Component {
  state={
    status:''
  }
  componentDidMount(){
    document.addEventListener('FBObjectReady',this.initialiseLogin)
  }
  componentWillUnmount() {
    document.removeEventListener('FBObjectReady',this.initialiseLogin)
  }

  initialiseLogin = () => {
    window.FB.getLoginStatus(response => this.statusChangeCallback(response));
    window.FB.Event.subscribe('auth.login', this.loginEvent);
    window.FB.Event.subscribe('auth.logout', this.logoutEvent); 
}

statusChangeCallback  = response => {console.log(response);this.setState({status:response.status})}
          loginEvent  = response => this.statusChangeCallback(response);
          logoutEvent = response => this.statusChangeCallback(response);

  render() {
    let button;
    (this.state.status === 'connected') ? 
      button = (<div>
        <button onClick={()=>window.FB.logout()}>Logout</button></div>) : 
      button = (<div><button 
        onClick={()=>window.FB.login()}>Login</button> </div>)
    return (
    <div> 
      <div> 
      {button}
        </div>
      {this.state.status}

          <div>
          </div>
    </div>
    )
  }
}
