import React, { Component } from 'react'

export default class AuthComponent extends Component {
  state={
    status:''
  }
  componentDidMount(){
    document.addEventListener('FBObjectReady',this.initialiseLogin)
  }
  componentWillUnmount() {
    document.removeEventListener('FBObjectReady',this.initialiseLogin)
  }

initialiseLogin = () => {
  window.FB.getLoginStatus(response => this.statusChangeCallback(response));
  window.FB.Event.subscribe('auth.login', this.login_event);
  window.FB.Event.subscribe('auth.logout', this.logout_event); 
}

statusChangeCallback = response => this.setState({status:response.status});
 loginEvent = response => this.statusChangeCallback(response);
 logoutEvent = (response) => {
  this.statusChangeCallback(response);
}
  render() {
    return (
    <div> 
      <div>
           <input type="button" value="Login" onClick={()=>window.FB.login()}/>
           <input type="button" value="Logout" onClick={()=>window.FB.logout()}/>
      </div>
    </div>
    )
  }
}
