import React, { Component } from 'react'
export default class AuthComponent extends Component {
    state = {status:''}
    componentDidMount(){
        (function(d, s, id){
            var js, fjs = d.getElementsByTagName(s)[0];
            if (d.getElementById(id)) {return;}
            js = d.createElement(s); js.id = id;
            js.src = 'https://connect.facebook.net/en_GB/sdk.js#xfbml=1&version=v3.2&appId=2000847070214153';
            fjs.parentNode.insertBefore(js, fjs);
          }(document, 'script', 'facebook-jssdk'));

          window.fbAsyncInit = () => {
                const  {init,getLoginStatus} = window.FB;
                init({
                  appId            : '2000847070214153',
                  autoLogAppEvents : true,
                  xfbml            : true,
                  version          : 'v3.2',
                  frictionlessRequests:true
                });
              getLoginStatus(response => this.statusChangeCallback(response))
            };
        }

        statusChangeCallback = response => {
                this.setState({status:response.status})
        }
