import React, { Component } from 'react'
import FBButton from './FBButton';
export default class AuthComponent extends Component {
  state = {
    status:''
  }
    componentDidMount() {
      document.addEventListener("FBObjectReady",this.initLogin)

    }

    componentWillUnmount() {
      document.removeEventListener("FBObjectReady",this.initLogin)
    }

  initLogin = () => {
    window.FB.getLoginStatus(response => this.onStatusChange(response))
  }

onStatusChange = (response)=>{
  let status = {
    ...response,
    status:status
  }
  this.setState({status})
    console.log(this.state.status);
}
        render() {
    return (
      <div>
        {
            (this.state.status === 'connected') ? <div>
              <h1>Welcome</h1>
            </div> :
           <div>
            <FBButton /><br/>
            <h1>{this.state.status}</h1>
          </div>
        }
      </div>
    )
  }
}
