import React, { Component } from 'react'
import FBButton from './FBButton';
export default class AuthComponent extends Component {
    state = {
      userID:''
    }
    componentDidMount() {
          window.fbAsyncInit = () => {
                const  {init,Event,api} = window.FB;
                init({
                  appId            : '2000847070214153',
                  autoLogAppEvents : true,
                  xfbml            : true,
                  version          : 'v3.2',
                  frictionlessRequests:true
                });
                api("/{985614108296889}/accounts", response=> this.pageResponse(response));
                Event.subscribe('auth.statusChange',response => this.demo(response));
                Event.subscribe('xfbml.render',this.finishedRendering());
              };
        }
    componentWillUnmount(){

    }
        pageResponse = (response) => {
          if (response && !response.error) {
            console.log("pageresponse",response)
          }
        }
        finishedRendering = () => {
          console.log("finished rendering plugins");
          var spinner = document.getElementById("spinner");
          spinner.removeAttribute("style");
          spinner.removeChild(spinner.childNodes[0]);        
        }
        demo = (response)=> {
          this.props.sendData(response.status);

          if(response.status === 'connected')
              this.setState({userID:response.authResponse.userID})
           // console.log(this.state.userID)
            }
            logOutClick =() =>{
              window.FB.logout(response=>console.log(response))
            }
        render() {
    return (
      <div>
        <FBButton/><br/>

      </div>
    )
  }
}
